mapper.select:
select * from (
	select k.*, rownum rnum from (
		select  from t_remote_upload_file_info t where 1 = 1

			and t.METHOD = #{method,jdbcType=VARCHAR}


			and t.IP like '%'||#{ip,jdbcType=VARCHAR}||'%'


			and t.USER_NAME like '%'||#{userName,jdbcType=VARCHAR}||'%'


			and t.ROUTE like '%'||#{route,jdbcType=VARCHAR}||'%'

		order by t.CREATE_TIME desc
	) k where rownum <= #{end}
) where rnum > #{start}
